import React from 'react'
import { Configuration, OpenAIApi } from 'openai-api'
import OpenAI from 'openai';

const openai = new OpenAI({
  apiKey: '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',
  dangerouslyAllowBrowser: true // This is also the default, can be omitted
});

export default function AIAssistance({solution, guesses}) {
    console.log(solution, guesses)
    const chatCompletion = async() => {
        let response = await openai.chat.completions.create({
            model: "gpt-3.5-turbo",
            messages: [{"role": "user", "content": "we are playing a game where I have to guess a 5 letter secret word. You know the word, but I do not. I need your help in giving me hints about the secret word without actually revealing the word itself. You will help me by relating the most previous word that I just guessed with the secret word. Again, you will not reveal the word. For example, if my most recent guess was spoon, and the word is pasta, you would give a hint such as \"your guess spoon relates to the hidden word in that they both pertain to food. the difference is that spoon is a possible vessel in which you can eat the secret word with.\" for this scenario, my most recent guess was ocean, and the secret word is razor. please give me a hint, using the instructions I just gave, without revealing the secret word razor."}],
            temperature: 0.2,
            max_tokens: 1000,
            frequency_penalty: 0.0
        });
        // console.log(response)
        // console.log(response['choices'][0]['message']['content']);
    }
    chatCompletion()
  return (
    <div>
        AIAssistance
    </div>
  )
}
