import React from 'react'
import { Configuration, OpenAIApi } from 'openai-api'
import OpenAI from 'openai';

const openai = new OpenAI({
  apiKey: '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',
  dangerouslyAllowBrowser: true // This is also the default, can be omitted
});

export default function AIAssistance({solution, guesses, turn}) {
    const helpButton = document.querySelector("#help-button");

    let clicked = false;
    let flipped = false;

    helpButton.onPress = () => {
        clicked ^= 1;
        flipped ^= 1 & clicked;

        const nextClass      = flipped ? "kf3" : "kf1";
        helpButton.className = clicked ? "kf2" : nextClass;
    };
    let mostRecent = guesses[turn-1].map((l, i) => {
        return l.key
    }).join('')
    const chatCompletion = async() => {
        let response = await openai.chat.completions.create({
            model: "gpt-3.5-turbo",
            messages: [{"role": "user", "content": `we are playing a game where I have to guess a 5 letter secret word. You know the word, but I do not. I need your help in giving me hints about the secret word without actually revealing the word itself. You will help me by relating the most previous word that I just guessed with the secret word. Again, you will not reveal the word. For example, if my most recent guess was spoon, and the word is pasta, you would give a hint such as \"your guess spoon relates to the hidden word in that they both pertain to food. the difference is that spoon is a possible vessel in which you can eat the secret word with.\" for this scenario, my most recent guess was ${mostRecent}, and the secret word is ${solution}. please give me a hint, using the instructions I just gave, without revealing the secret word ${solution}. It is incredibly important that you response does not include the word ${solution}!`}],
            temperature: 0.2,
            max_tokens: 1000,
            frequency_penalty: 0.0
        });
        return response['choices'][0]['message']['content']
    }
    chatCompletion()
  return (
    <div>
        <button id="help-button" type="button" ontouchstart="">

        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100" height="100" width="100" class="kf1">

            <path id="dot" d="m 48.022097,82.90284 c 1.192314,-0.02147 2.173956,-0.831358 2.185365,-1.947038 0.01141,-1.11568 -0.973891,-2.270335 -2.185365,-2.278068 -1.247604,-0.008 -2.049902,1.054459 -2.03911,2.278072 0.01079,1.223613 1.240139,1.949581 1.984264,1.946573 C 57.764626,82.851586 72.086342,84.492783 81.015625,76 91.923664,65.625191 88.415613,45.865135 71.020037,29.046254 L 30.007844,69.947962 c -3.245469,3.236726 -4.128897,8.694076 0.573223,11.799844 4.30204,2.841515 11.355956,1.079354 17.44103,1.155034 z" style="opacity:1;fill:none;stroke-linecap:round;stroke-linejoin:round;stroke-miterlimit:4;stroke-opacity:1" />
            <path id="qmark" d="M 29.387754,29.397748 70.99794,71.030719 C 85.063599,67.076558 71.518598,50.054823 65.044194,64.589689 61.518139,72.50559 47.419888,77.236862 48,66.0625 L 47.97278,56.999069 C 52.628822,58.147585 74.845345,50.150007 66.202876,27.78672 61.644451,15.991323 38.55285,10.038018 29.387754,29.397748 Z" style="fill:none;stroke-linecap:round;stroke-linejoin:round;stroke-miterlimit:4	;stroke-opacity:1" />
        </svg>

        </button>
    </div>
  )
}
