import React from 'react'
import { useEffect, useState } from 'react'

export default function InstructionModal() {
    const [show, setShow] = useState(true)
    console.log(show)
    const exit = () => {
        setShow(false);
    }
    if (show) {  
        return (
            <div className="modal">
              Welcome!
              <button onClick={exit()}/>
            </div>
          )
    }
    else {
        return (
            <div></div>
        )
    }
}
